// Obtener El inicio y el fin de mes paasado
const now = new Date();
const monthNames = ["Enero", "Febrero", "Marzo", "Abril", "Mayo", "Junio", "Julio", "Agosto", "Septiembre", "Octubre", "Noviembre", "Diciembre"];
const startOfCurrentMonth = new Date(now.getFullYear(), now.getMonth(), 1);
const endOfLastMonth = new Date(startOfCurrentMonth);
endOfLastMonth.setDate(endOfLastMonth.getDate() - 1);
const startOfLastMonth = new Date(endOfLastMonth.getFullYear(), endOfLastMonth.getMonth(), 1);
// Variables de Fechas:
const startOfLastMonthISO = startOfLastMonth.toISOString();
const endOfLastMonthISO = endOfLastMonth.toISOString();
const lastMonthName = monthNames[startOfLastMonth.getMonth()];
const Year = startOfLastMonth.getFullYear();
const notebook_name = `Reporte ${lastMonthName} ${Year}`
const type = "notebook";

//console.log("Inicio del mes pasado (ISO):", startOfLastMonthISO);
//console.log("Fin del mes pasado (ISO):", endOfLastMonthISO);
//console.log("Mes pasado en texto:", lastMonthName);

// Definir el hostname y el hostId como variables
//let hostname = "CCPPALCW01";
//let hostId = "HOST-081F3F040241A45B";



// JSON base con la versión y el periodo de tiempo por defecto
let jsonData = {
  version: "6",
  defaultTimeframe: { from: startOfLastMonthISO, to: endOfLastMonthISO },
  sections: []
};
let sectionMarkdown, sectionDQL_MaxCPU, sectionDQL_Disk, sectionDQL_Memory;

//jsonData = reporte_server(jsonData, hostname, hostId, startOfLastMonthISO, endOfLastMonthISO);
//const content2 = new Blob([JSON.stringify(jsonData, null, 2)], { type: "application/json" });

export default async function () {
    const timeout = 60;
    const query = 'fetch dt.entity.host';
    const response = await queryExecutionClient.queryExecute({ body: { query, requestTimeoutMilliseconds: timeout * 1000, fetchTimeoutSeconds: timeout  } });
    response.result.records.forEach(item => { jsonData = reporte_server(jsonData, `${item["entity.name"]}`, `${item.id}`, startOfLastMonthISO, endOfLastMonthISO);
    });
    console.log("JSON Final:", JSON.stringify(jsonData, null, 2));
    const content2 = new Blob([JSON.stringify(jsonData, null, 2)], { type: "application/json" });
    const data2 = await documentsClient.createDocument({
      body: {
        type: type,
        name: notebook_name,
        content: content2,  
      },
    });

    const data = await directSharesClient.createDirectShare({
      body: {
        documentId: data2.id,
        access: "read-write",
        recipients: [
          {
            "id": "1f7b6e71-7f4a-4d21-bdef-d624eed30a5c",
            "type": "group",
          },
        ],
      },
    });

    return `https://gmc77208.apps.dynatrace.com/ui/apps/dynatrace.notebooks/notebook/${data2.id}`;
}

